var React = require('react');
var hljs = require('highlight.js/lib/highlight.js');
hljs.registerLanguage("erlang-repl", require("highlight.js/lib/languages/erlang-repl.js"));

module.exports = React.createClass({
  displayName: 'HighLight-' + "erlang-repl",
  render: function () {
    var children = this.props.children
    if (typeof children !== 'string') children = React.Children.only(children)
    return React.createElement('pre', { className: 'hljs ' + "erlang-repl" },
      React.createElement('code', {
        className: 'hljs ' + "erlang-repl",
        dangerouslySetInnerHTML: {
          __html: hljs.highlight("erlang-repl", children, true).value
        }
      })
    );
  }
});
