var React = require('react');
var hljs = require('highlight.js/lib/highlight.js');
hljs.registerLanguage("perl", require("highlight.js/lib/languages/perl.js"));

module.exports = React.createClass({
  displayName: 'HighLight-' + "perl",
  render: function () {
    var children = this.props.children
    if (typeof children !== 'string') children = React.Children.only(children)
    return React.createElement('pre', { className: 'hljs ' + "perl" },
      React.createElement('code', {
        className: 'hljs ' + "perl",
        dangerouslySetInnerHTML: {
          __html: hljs.highlight("perl", children, true).value
        }
      })
    );
  }
});
