var React = require('react');
var hljs = require('highlight.js/lib/highlight.js');
hljs.registerLanguage("rust", require("highlight.js/lib/languages/rust.js"));

module.exports = React.createClass({
  displayName: 'HighLight-' + "rust",
  render: function () {
    var children = this.props.children
    if (typeof children !== 'string') children = React.Children.only(children)
    return React.createElement('pre', { className: 'hljs ' + "rust" },
      React.createElement('code', {
        className: 'hljs ' + "rust",
        dangerouslySetInnerHTML: {
          __html: hljs.highlight("rust", children, true).value
        }
      })
    );
  }
});
