var React = require('react');
var hljs = require('highlight.js/lib/highlight.js');
hljs.registerLanguage("vbscript-html", require("highlight.js/lib/languages/vbscript-html.js"));

module.exports = React.createClass({
  displayName: 'HighLight-' + "vbscript-html",
  render: function () {
    var children = this.props.children
    if (typeof children !== 'string') children = React.Children.only(children)
    return React.createElement('pre', { className: 'hljs ' + "vbscript-html" },
      React.createElement('code', {
        className: 'hljs ' + "vbscript-html",
        dangerouslySetInnerHTML: {
          __html: hljs.highlight("vbscript-html", children, true).value
        }
      })
    );
  }
});
